                    <Label text="üìã Danh s√°ch s·∫£n ph·∫©m"
                           style="-fx-font-size: 20px;
                                  -fx-font-weight: bold;
                                  -fx-text-fill: #8B4513;" />
                    <Region HBox.hgrow="ALWAYS" />
                    <TextField fx:id="txtSearch"
                               promptText="üîç T√¨m ki·∫øm s·∫£n ph·∫©m..."
                               prefWidth="250"
                               style="-fx-background-radius: 20;
                                      -fx-border-color: #D2B48C;
                                      -fx-border-radius: 20;
                                      -fx-padding: 10;
                                      -fx-font-size: 14px;" />
                </HBox>

                <TableView fx:id="tableProducts"
                           prefHeight="350"
                           style="-fx-background-color: #FAFAFA;
                                  -fx-background-radius: 10;
                                  -fx-border-color: #D2B48C;
                                  -fx-border-radius: 10;">
