                          style="-fx-border-color: #D2B48C;
                                 -fx-border-radius: 10;
                                 -fx-background-color: #FAFAFA;
                                 -fx-background-radius: 10;
                                 -fx-padding: 15;">
                        <ImageView fx:id="imgPreview"
                                   fitWidth="120" fitHeight="120"
                                   preserveRatio="true"
                                   style="-fx-effect: dropshadow(gaussian, rgba(0,0,0,0.2), 5, 0, 0, 2);"/>
                        <Button text="üì∑ Ch·ªçn ·∫£nh"
