package com.coffeeshop.coffeeshop.controller;

import com.coffeeshop.coffeeshop.dao.OrderDao;
import com.coffeeshop.coffeeshop.dao.ProductDAO;
import com.coffeeshop.coffeeshop.dao.CategoriesDao;
import com.coffeeshop.coffeeshop.model.Order;
import com.coffeeshop.coffeeshop.model.OrderDetail;
import com.coffeeshop.coffeeshop.model.Product;
import com.coffeeshop.coffeeshop.model.Categories;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.fxml.FXML;
import javafx.scene.control.*;
import javafx.scene.control.cell.PropertyValueFactory;
import javafx.scene.layout.FlowPane;
import javafx.scene.layout.VBox;
import javafx.scene.layout.HBox;
import javafx.scene.layout.Region;
import javafx.geometry.Insets;
import javafx.geometry.Pos;

import java.sql.Connection;
import java.sql.SQLException;
import java.time.LocalDateTime;
import java.util.ArrayList;
import java.util.List;

public class OrderController {

    @FXML private ComboBox<String> cbCategory;
    @FXML private FlowPane productsFlowPane;

    @FXML private TableView<OrderDetail> tblOrderDetails;
    @FXML private TableColumn<OrderDetail, Integer> colProductID;
    @FXML private TableColumn<OrderDetail, Integer> colQuantity;
    @FXML private TableColumn<OrderDetail, Double> colPrice;

    @FXML private ComboBox<String> cbCustomer;
    @FXML private Label lblTotal;
    @FXML private Label lblMessage;

    private ObservableList<OrderDetail> orderDetailList = FXCollections.observableArrayList();
    private OrderDao orderDao;
    private ProductDAO productDAO;
    private CategoriesDao categoriesDAO;
    private Connection connection;

    private List<Product> allProducts = new ArrayList<>();
    private List<Categories> allCategories = new ArrayList<>();

    public void setConnection(Connection connection) {
        this.connection = connection;
        this.orderDao = new OrderDao(connection);
        this.productDAO = new ProductDAO(connection);
        this.categoriesDAO = new CategoriesDao(connection);
    }

    @FXML
    public void initialize() {
        setupTable();
        setupCustomerComboBox();

        // Load d·ªØ li·ªáu v√† render s·∫£n ph·∫©m
        loadData();

        updateTotal();
    }

    private void setupTable() {
        colProductID.setCellValueFactory(new PropertyValueFactory<>("productId"));
        colQuantity.setCellValueFactory(new PropertyValueFactory<>("quantity"));
        colPrice.setCellValueFactory(new PropertyValueFactory<>("unitPrice"));
        tblOrderDetails.setItems(orderDetailList);
    }

    private void setupCustomerComboBox() {
        cbCustomer.setItems(FXCollections.observableArrayList("Kh√°ch l·∫ª", "Th·∫ª th√†nh vi√™n", "Kh√°ch VIP"));
        cbCustomer.getSelectionModel().selectFirst();
    }

    private void loadData() {
        try {
            // Load categories
            allCategories = categoriesDAO.getAllCategories();
            ObservableList<String> categoryNames = FXCollections.observableArrayList();
            categoryNames.add("T·∫•t c·∫£");
            for (Categories cat : allCategories) {
                categoryNames.add(cat.getNameCategories());
            }
            cbCategory.setItems(categoryNames);
            cbCategory.getSelectionModel().selectFirst();

            // Load products
            allProducts = productDAO.getAllProducts();

            // Setup category filter
            cbCategory.setOnAction(e -> filterAndRenderProducts());

            // Initial render
            filterAndRenderProducts();

        } catch (SQLException e) {
            lblMessage.setText("‚ùå L·ªói khi t·∫£i d·ªØ li·ªáu: " + e.getMessage());
            e.printStackTrace();
        }
    }

    private void filterAndRenderProducts() {
        String selectedCategory = cbCategory.getSelectionModel().getSelectedItem();
        List<Product> filteredProducts = new ArrayList<>();

        if ("T·∫•t c·∫£".equals(selectedCategory)) {
            filteredProducts = allProducts;
        } else {
            for (Product product : allProducts) {
                if (product.getCategories().getNameCategories().equals(selectedCategory)) {
                    filteredProducts.add(product);
                }
            }
        }

        renderProducts(filteredProducts);
    }

    private void renderProducts(List<Product> products) {
        productsFlowPane.getChildren().clear();

        for (Product product : products) {
            if (product.isActive()) { // Ch·ªâ hi·ªÉn th·ªã s·∫£n ph·∫©m active
                Button productButton = createProductButton(product);
                productsFlowPane.getChildren().add(productButton);
            }
        }
    }

    private Button createProductButton(Product product) {
        Button button = new Button();
        button.setPrefSize(150, 100);
        button.setMaxSize(150, 100);
        button.setMinSize(150, 100);

        // Create content for button
        VBox content = new VBox(5);
        content.setAlignment(Pos.CENTER);

        // Product name
        Label nameLabel = new Label(product.getNameProducts());
        nameLabel.setStyle("-fx-font-weight: bold; -fx-font-size: 12px; -fx-text-fill: #8B4513;");
        nameLabel.setWrapText(true);
        nameLabel.setMaxWidth(140);

        // Product price
        Label priceLabel = new Label(String.format("%,.0f VND", product.getPrice()));
        priceLabel.setStyle("-fx-font-size: 11px; -fx-text-fill: #B8860B; -fx-font-weight: bold;");

        // Category
        Label categoryLabel = new Label(product.getCategories().getNameCategories());
        categoryLabel.setStyle("-fx-font-size: 10px; -fx-text-fill: #666666;");

        content.getChildren().addAll(nameLabel, priceLabel, categoryLabel);

        // Button styling
        button.setStyle(
                "-fx-background-color: linear-gradient(to bottom, #FFFFFF, #F5F5F5); " +
                        "-fx-border-color: #CD853F; " +
                        "-fx-border-radius: 8; " +
                        "-fx-background-radius: 8; " +
                        "-fx-padding: 8; " +
                        "-fx-effect: dropshadow(gaussian, rgba(0,0,0,0.2), 3, 0, 0, 1);"
        );

        // Hover effect
        button.setOnMouseEntered(e -> button.setStyle(
                "-fx-background-color: linear-gradient(to bottom, #F0F8FF, #E6F3FF); " +
                        "-fx-border-color: #8B4513; " +
                        "-fx-border-radius: 8; " +
                        "-fx-background-radius: 8; " +
                        "-fx-padding: 8; " +
                        "-fx-effect: dropshadow(gaussian, rgba(0,0,0,0.3), 5, 0, 0, 2);"
        ));

        button.setOnMouseExited(e -> button.setStyle(
                "-fx-background-color: linear-gradient(to bottom, #FFFFFF, #F5F5F5); " +
                        "-fx-border-color: #CD853F; " +
                        "-fx-border-radius: 8; " +
                        "-fx-background-radius: 8; " +
                        "-fx-padding: 8; " +
                        "-fx-effect: dropshadow(gaussian, rgba(0,0,0,0.2), 3, 0, 0, 1);"
        ));

        // Set content
        button.setGraphic(content);

        // Click handler
        button.setOnAction(e -> handleProductClick(product));

        return button;
    }

    private void handleProductClick(Product product) {
        // T·∫°o dialog ƒë·ªÉ ch·ªçn s·ªë l∆∞·ª£ng v√† size
        Dialog<ButtonType> dialog = new Dialog<>();
        dialog.setTitle("Th√™m v√†o ƒë∆°n h√†ng");
        dialog.setHeaderText("S·∫£n ph·∫©m: " + product.getNameProducts());

        // Create dialog content
        VBox dialogContent = new VBox(10);
        dialogContent.setPadding(new Insets(20));

        // Quantity
        HBox quantityBox = new HBox(10);
        quantityBox.setAlignment(Pos.CENTER_LEFT);
        Label quantityLabel = new Label("S·ªë l∆∞·ª£ng:");
        quantityLabel.setPrefWidth(80);
        Spinner<Integer> quantitySpinner = new Spinner<>(1, 99, 1);
        quantitySpinner.setPrefWidth(100);
        quantityBox.getChildren().addAll(quantityLabel, quantitySpinner);

        // Size
        HBox sizeBox = new HBox(10);
        sizeBox.setAlignment(Pos.CENTER_LEFT);
        Label sizeLabel = new Label("Size:");
        sizeLabel.setPrefWidth(80);
        ComboBox<String> sizeComboBox = new ComboBox<>();
        sizeComboBox.getItems().addAll("S", "M", "L");
        sizeComboBox.getSelectionModel().selectFirst();
        sizeComboBox.setPrefWidth(100);
        sizeBox.getChildren().addAll(sizeLabel, sizeComboBox);

        // Price info
        Label priceInfo = new Label("Gi√°: " + String.format("%,.0f VND", product.getPrice()));
        priceInfo.setStyle("-fx-font-weight: bold; -fx-text-fill: #8B4513;");

        dialogContent.getChildren().addAll(quantityBox, sizeBox, priceInfo);
        dialog.getDialogPane().setContent(dialogContent);

        // Add buttons
        dialog.getDialogPane().getButtonTypes().addAll(ButtonType.OK, ButtonType.CANCEL);

        // Show dialog and handle result
        dialog.showAndWait().ifPresent(result -> {
            if (result == ButtonType.OK) {
                int quantity = quantitySpinner.getValue();
                String size = sizeComboBox.getValue();
                addProductToOrder(product.getId(), quantity, size, product.getPrice());

                lblMessage.setText("‚úÖ ƒê√£ th√™m " + product.getNameProducts() + " x" + quantity + " v√†o ƒë∆°n h√†ng");
            }
        });
    }

    /** G·ªçi khi ng∆∞·ªùi d√πng b·∫•m n√∫t "üìã In H√≥a ƒê∆°n" */
    @FXML
    private void handleSaveOrder() {
        if (orderDetailList.isEmpty()) {
            lblMessage.setText("Ch∆∞a c√≥ m√≥n n√†o trong ƒë∆°n.");
            return;
        }

        Order order = new Order();
        order.setStaffId(1); // T·∫°m th·ªùi c·ªë ƒë·ªãnh
        order.setOrderDate(LocalDateTime.now());
        order.setStatus("Ch·ªù x·ª≠ l√Ω");
        order.setNote(cbCustomer.getValue());

        List<OrderDetail> details = new ArrayList<>(orderDetailList);

        boolean success = orderDao.addOrder(order, details);
        if (success) {
            lblMessage.setText("‚úÖ ƒê∆°n h√†ng ƒë√£ ƒë∆∞·ª£c t·∫°o! M√£: " + order.getOrderId());
            orderDetailList.clear();
            updateTotal();
        } else {
            lblMessage.setText("‚ùå L·ªói khi t·∫°o ƒë∆°n h√†ng.");
        }
    }

    /** H√†m d√πng khi b·∫°n nh·∫•n v√†o 1 s·∫£n ph·∫©m ƒë·ªÉ th√™m v√†o ƒë∆°n */
    public void addProductToOrder(int productId, int quantity, String size, double price) {
        // T√¨m xem s·∫£n ph·∫©m ƒë√£ c√≥ trong ƒë∆°n ch∆∞a
        OrderDetail existingDetail = null;
        for (OrderDetail detail : orderDetailList) {
            if (detail.getProductId() == productId && size.equals(detail.getSize())) {
                existingDetail = detail;
                break;
            }
        }

        if (existingDetail != null) {
            // N·∫øu ƒë√£ c√≥, tƒÉng s·ªë l∆∞·ª£ng
            existingDetail.setQuantity(existingDetail.getQuantity() + quantity);
        } else {
            // N·∫øu ch∆∞a c√≥, t·∫°o m·ªõi
            OrderDetail d = new OrderDetail();
            d.setProductId(productId);
            d.setQuantity(quantity);
            d.setSize(size);
            d.setUnitPrice(price);
            orderDetailList.add(d);
        }

        updateTotal();

        // Refresh table
        tblOrderDetails.refresh();
    }

    private void updateTotal() {
        double total = orderDetailList.stream()
                .mapToDouble(d -> d.getQuantity() * d.getUnitPrice())
                .sum();
        lblTotal.setText(String.format("%,.0f VND", total));
    }

    /** Quay l·∫°i (n·∫øu c√≥ ch·ª©c nƒÉng) */
    @FXML
    private void handleBack() {
        System.out.println("Quay l·∫°i m√†n h√¨nh ch√≠nh");
    }
}
